'use client';
import { useState, useEffect, useMemo } from 'react'; // Added useMemo
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"; // Import Select
import { Button } from '@/components/ui/button';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import {
  BarChart, Bar, XAxis, YAxis, Tooltip, Legend, ResponsiveContainer, CartesianGrid, PieChart, Pie, Cell
} from 'recharts';
import { ReviewReportKpi, ReviewReportDataItem, RatingDistributionChartItem } from '../action/getReviewsReportData';
import { Star } from 'lucide-react'; // For displaying star ratings

interface ReviewsReportClientProps {
  kpis: ReviewReportKpi[];
  reviews: ReviewReportDataItem[];
  ratingDistributionChart: RatingDistributionChartItem[];
  initialFrom?: string;
  initialTo?: string;
  error?: string;
}

// Helper to render stars
const renderStars = (rating: number) => {
  const stars = [];
  for (let i = 1; i <= 5; i++) {
    stars.push(
      <Star
        key={i}
        size={16}
        className={i <= rating ? 'fill-primary text-primary' : 'fill-muted text-muted-foreground'} // Use semantic colors
      />
    );
  }
  return <div className="flex">{stars}</div>;
};


export default function ReviewsReportClient({
  kpis,
  reviews,
  ratingDistributionChart,
  initialFrom,
  initialTo,
  error,
}: ReviewsReportClientProps) {
  const [from, setFrom] = useState(initialFrom || '');
  const [to, setTo] = useState(initialTo || '');

  const [page, setPage] = useState(1);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [searchTerm, setSearchTerm] = useState('');
  const [starFilter, setStarFilter] = useState<string>(''); // '' for All Stars

  const [barChartColor, setBarChartColor] = useState('#8884d8');
  const [pieChartColors, setPieChartColors] = useState<string[]>(['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#FFBB28']);


  function getResolvedColor(variableName: string, fallbackColor: string): string {
    if (typeof window === 'undefined') return fallbackColor;
    const value = getComputedStyle(document.documentElement).getPropertyValue(variableName).trim();
    if (value) return `hsl(${value.replace(/\s+/g, ', ')})`;
    return fallbackColor;
  }

  useEffect(() => {
    setBarChartColor(getResolvedColor('--chart-1', '#8884d8'));
    const resolvedPieColors = [
      getResolvedColor('--chart-1', '#0088FE'), // 5 stars
      getResolvedColor('--chart-2', '#00C49F'), // 4 stars
      getResolvedColor('--chart-3', '#FFBB28'), // 3 stars
      getResolvedColor('--chart-4', '#FF8042'), // 2 stars
      getResolvedColor('--chart-5', '#F43F5E'), // 1 star (using chart-5, which might be red)
    ].filter(Boolean);
    if (resolvedPieColors.length === 5) setPieChartColors(resolvedPieColors); // Expect 5 colors for 5 stars
  }, []);

  const handleFilter = () => {
    const params = new URLSearchParams();
    if (from) params.set('from', from);
    if (to) params.set('to', to);
    window.location.search = params.toString();
  };

  if (error) {
    return <Card><CardContent className="py-10 text-center text-destructive-foreground">{error}</CardContent></Card>;
  }

  // Filtered & paginated reviews
  const filteredReviews = useMemo(() => {
    let tempReviews = reviews;

    if (starFilter) {
      const numericStarFilter = parseInt(starFilter, 10);
      tempReviews = tempReviews.filter(review => review.rating === numericStarFilter);
    }

    if (!searchTerm) return tempReviews;
    const lowerSearchTerm = searchTerm.toLowerCase();
    return tempReviews.filter(
      (review) =>
        review.productName?.toLowerCase().includes(lowerSearchTerm) ||
        review.userName?.toLowerCase().includes(lowerSearchTerm) ||
        review.comment?.toLowerCase().includes(lowerSearchTerm)
    );
  }, [reviews, searchTerm, starFilter]);

  const totalPages = Math.ceil(filteredReviews.length / rowsPerPage) || 1;
  const paginatedReviews = useMemo(() => {
    const start = (page - 1) * rowsPerPage;
    return filteredReviews.slice(start, start + rowsPerPage);
  }, [filteredReviews, page, rowsPerPage]);

  // Reset page if filter changes
  useEffect(() => {
    setPage(1);
  }, [searchTerm, rowsPerPage, starFilter]);


  return (
    <div className='space-y-8'>
      {/* Filter Panel */}
      <Card>
        <CardContent className='flex flex-col items-end gap-4 py-4 md:flex-row'>
          <div>
            <label className='mb-1 block text-sm font-medium text-card-foreground'>┘à┘å ╪¬╪º╪▒┘è╪«</label>
            <Input type='date' value={from} onChange={(e) => setFrom(e.target.value)} />
          </div>
          <div>
            <label className='mb-1 block text-sm font-medium text-card-foreground'>╪Ñ┘ä┘ë ╪¬╪º╪▒┘è╪«</label>
            <Input type='date' value={to} onChange={(e) => setTo(e.target.value)} />
          </div>
          <Button className='mt-4 md:mt-0' onClick={handleFilter}>
            ╪¬╪╖╪¿┘è┘é ╪º┘ä┘ü┘ä╪¬╪▒
          </Button>
        </CardContent>
      </Card>

      {/* KPIs */}
      {kpis.length > 0 ? (
        <div className='grid grid-cols-1 gap-4 sm:grid-cols-2 md:grid-cols-4'>
          {kpis.map((kpi) => (
            <Card key={kpi.label} className='text-center bg-card shadow'>
              <CardHeader>
                <CardTitle className='text-lg font-semibold text-card-foreground'>{kpi.label}</CardTitle>
              </CardHeader>
              <CardContent>
                <p className='text-3xl font-bold text-primary'>{String(kpi.value)}</p>
              </CardContent>
            </Card>
          ))}
        </div>
      ) : (
        <Card><CardContent className="py-4 text-center text-muted-foreground">┘ä╪º ╪¬┘ê╪¼╪» ╪¿┘è╪º┘å╪º╪¬ KPI ┘ä┘ä╪╣╪▒╪╢.</CardContent></Card>
      )}

      {/* Rating Distribution Chart */}
      <Card>
        <CardHeader><CardTitle className="text-xl text-card-foreground">╪¬┘ê╪▓┘è╪╣ ╪º┘ä╪¬┘é┘è┘è┘à╪º╪¬</CardTitle></CardHeader>
        <CardContent>
          {ratingDistributionChart.reduce((sum, item) => sum + item.count, 0) > 0 ? (
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={ratingDistributionChart} layout="vertical">
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis type="number" />
                <YAxis type="category" dataKey="name" width={80} interval={0} />
                <Tooltip />
                <Legend />
                <Bar dataKey="count" name="╪╣╪»╪» ╪º┘ä╪¬┘é┘è┘è┘à╪º╪¬" fill={barChartColor} />
              </BarChart>
            </ResponsiveContainer>
          ) : (
            <p className="text-center text-muted-foreground py-10">┘ä╪º ╪¬┘ê╪¼╪» ╪¿┘è╪º┘å╪º╪¬ ┘ä╪¬┘ê╪▓┘è╪╣ ╪º┘ä╪¬┘é┘è┘è┘à╪º╪¬.</p>
          )}
        </CardContent>
      </Card>

      {/* Reviews Table */}
      <Card>
        <CardHeader className="flex flex-row items-center justify-between space-y-0">
          <CardTitle className="text-xl text-card-foreground">┘é╪º╪ª┘à╪⌐ ╪º┘ä╪¬┘é┘è┘è┘à╪º╪¬</CardTitle>
          <div className="flex items-center gap-2">
            <Input
              type="search"
              placeholder="╪º╪¿╪¡╪½ ┘ü┘è ╪º┘ä╪¬┘é┘è┘è┘à╪º╪¬..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="max-w-sm" // Slightly wider search
            />
            <Select
              value={starFilter}
              onValueChange={(value) => setStarFilter(value === 'all' ? '' : value)}
            >
              <SelectTrigger id="starFilterSelect" className="w-[120px]">
                <SelectValue placeholder="┘ü┘ä╪¬╪▒ ╪º┘ä┘å╪¼┘ê┘à" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">┘â┘ä ╪º┘ä┘å╪¼┘ê┘à</SelectItem>
                <SelectItem value="5">5 ┘å╪¼┘ê┘à</SelectItem>
                <SelectItem value="4">4 ┘å╪¼┘ê┘à</SelectItem>
                <SelectItem value="3">3 ┘å╪¼┘ê┘à</SelectItem>
                <SelectItem value="2">2 ┘å╪¼┘ê┘à</SelectItem>
                <SelectItem value="1">1 ┘å╪¼┘à╪⌐</SelectItem>
              </SelectContent>
            </Select>
            <Select
              value={String(rowsPerPage)}
              onValueChange={(value) => setRowsPerPage(Number(value))}
            >
              <SelectTrigger id="reviewsRowsPerPage" className="w-[100px]">
                <SelectValue placeholder="╪╣╪»╪» ╪º┘ä╪╡┘ü┘ê┘ü" />
              </SelectTrigger>
              <SelectContent>
                {[10, 20, 50, 100].map((n) => (
                  <SelectItem key={n} value={String(n)}>
                    {n} ╪╡┘ü┘ê┘ü
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </CardHeader>
        <CardContent className='overflow-x-auto'>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead className="text-right">╪º┘ä┘à┘å╪¬╪¼</TableHead>
                <TableHead className="text-right">╪º┘ä┘à╪│╪¬╪«╪»┘à</TableHead>
                <TableHead className="text-right">╪º┘ä╪¬┘é┘è┘è┘à</TableHead>
                <TableHead className="text-right">╪º┘ä╪¬╪╣┘ä┘è┘é</TableHead>
                <TableHead className="text-right">╪º┘ä╪¬╪º╪▒┘è╪«</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {paginatedReviews.length > 0 ? paginatedReviews.map((review) => (
                <TableRow key={review.id}><TableCell className="font-medium">{review.productName}</TableCell><TableCell className="text-muted-foreground">{review.userName}</TableCell><TableCell>{renderStars(review.rating)}</TableCell><TableCell className="max-w-xs whitespace-normal break-words">{review.comment || '-'}</TableCell><TableCell className="text-muted-foreground">{new Date(review.createdAt).toLocaleDateString('ar-EG')}</TableCell></TableRow>
              )) : (
                <TableRow><TableCell colSpan={5} className="text-center text-muted-foreground py-10">{reviews.length === 0 ? '┘ä╪º ╪¬┘ê╪¼╪» ╪¬┘é┘è┘è┘à╪º╪¬ ┘ä╪╣╪▒╪╢┘ç╪º.' : '┘ä╪º ╪¬┘ê╪¼╪» ╪¬┘é┘è┘è┘à╪º╪¬ ╪¬╪╖╪º╪¿┘é ╪¿╪¡╪½┘â.'}</TableCell></TableRow>
              )}
            </TableBody>
          </Table>
        </CardContent>
        {totalPages > 1 && (
          <div className='flex items-center justify-center gap-2 border-t border-border p-4'>
            <Button variant='outline' size='sm' disabled={page === 1} onClick={() => setPage(page - 1)}>
              ╪º┘ä╪│╪º╪¿┘é
            </Button>
            <span className='mx-2 text-sm text-muted-foreground'>
              ╪╡┘ü╪¡╪⌐ {page} ┘à┘å {totalPages}
            </span>
            <Button
              variant='outline'
              size='sm'
              disabled={page === totalPages}
              onClick={() => setPage(page + 1)}
            >
              ╪º┘ä╪¬╪º┘ä┘è
            </Button>
          </div>
        )}
      </Card>
    </div>
  );
}

