            <div className='space-y-2'>
              <Label htmlFor='longitude'>╪Ñ╪»╪«╪º┘ä ╪«╪╖ ╪º┘ä╪╖┘ê┘ä</Label>
              <Input
                id='longitude'
                name='longitude'
                value={longitude}
                onChange={handleLongitudeChange}
