        // Assuming Prisma schema for hreflang is Json?
        return { success: false, message: 'Hreflang data is not valid JSON.', errors: { hreflang: ['Invalid JSON format for Hreflang.'] } };
      }
    } else if (validatedFields.data.hreflang) {
