                  </TableCell>
                  <TableCell className="text-right">
                    {/* IMPORTANT: Update this href to your actual category edit path */}
                    <Button asChild variant="outline" size="sm">
                      <Link href={`/seo/categories/edit/${category.id}`}>
                        Edit Category
                      </Link>
                    </Button>
                    <Button asChild variant="outline" size="sm">
                      <Link href={`/seo/categories/view/${category.id}`}>
                        view
                      </Link>
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      )}
      {/* TODO: Add pagination controls if many categories */}
    </div>
  );
}

'use client';

import type React from 'react';
import { useState } from 'react'; // Import useState
import { AdvancedSeoFormTooltips, SchemaOrgTemplates, IndustryDataTemplates } from '@/app/seo/constant/advanced-tooltip-content';
import InfoTooltip from '@/components/ui/InfoTooltip';
import { Button } from '@/components/ui/button'; // Import Button
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'; // Import Select
import { Textarea } from '@/components/ui/textarea';
import type { EntityType, IndustryType } from '@prisma/client'; // Import Enums

export type AdvancedSeoFormDataSubset = {
  schemaOrg: string;
  industryData: string;
};

type AdvancedSeoFormFieldsProps = {
  formData: AdvancedSeoFormDataSubset;
  errors: Record<string, string[] | string | undefined>;
  handleChange: (e: React.ChangeEvent<HTMLTextAreaElement>) => void;
  entityType?: EntityType; // Add entityType
  industryType?: IndustryType; // Add industryType
};

export default function AdvancedSeoFormFields({
  formData,
  errors,
  handleChange,
  entityType,
  industryType,
}: AdvancedSeoFormFieldsProps) {
  const [selectedSchemaTemplate, setSelectedSchemaTemplate] = useState('');
  const [selectedIndustryTemplate, setSelectedIndustryTemplate] = useState('');

  const handleTemplateSelect = (fieldName: 'schemaOrg' | 'industryData', value: string) => {
    handleChange({
      target: { name: fieldName, value },
    } as React.ChangeEvent<HTMLTextAreaElement>);
    if (fieldName === 'schemaOrg') setSelectedSchemaTemplate(value);
    if (fieldName === 'industryData') setSelectedIndustryTemplate(value);
  };

  const currentSchemaTemplates = entityType ? (SchemaOrgTemplates[entityType] || SchemaOrgTemplates['DEFAULT']) : SchemaOrgTemplates['DEFAULT'];
  const currentIndustryTemplates = industryType && IndustryDataTemplates[industryType] ? IndustryDataTemplates[industryType] : [];


  const isValidJson = (str: string | null | undefined): boolean => {
    if (str === null || str === undefined || str.trim() === '') return true;
    try {
      JSON.parse(str);
      return true;
    } catch {
      return false;
    }
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle>Schema.org Markup (JSON-LD)</CardTitle>
          <CardDescription>Provide structured data to help search engines understand your content.</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-2">
            <div className="flex items-center gap-1">
              <Label htmlFor='schemaOrg'>Schema.org JSON-LD</Label>
              <InfoTooltip content={AdvancedSeoFormTooltips.schemaOrg} />
            </div>
            <div className="flex items-center space-x-2 mb-2">
              <Select value={selectedSchemaTemplate} onValueChange={(value) => {
                if (value) handleTemplateSelect('schemaOrg', value);
                setSelectedSchemaTemplate(value);
              }}>
                <SelectTrigger className="w-[300px]">
                  <SelectValue placeholder="Select a Schema.org template (optional)" />
                </SelectTrigger>
                <SelectContent>
                  {currentSchemaTemplates.map((template: { label: string; value: string }) => (
                    <SelectItem key={template.label} value={template.value}>{template.label}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <Textarea
              id='schemaOrg'
              name='schemaOrg'
              value={formData.schemaOrg || ''}
              onChange={handleChange}
              rows={10}
              className={`${errors.schemaOrg || !isValidJson(formData.schemaOrg) ? 'border-destructive' : ''} font-mono text-sm`}
              placeholder='{\n  "@context": "https://schema.org",\n  "@type": "WebPage",\n  "name": "Page Title"\n}'
            />
            {errors.schemaOrg && <p className='text-sm text-destructive'>{Array.isArray(errors.schemaOrg) ? errors.schemaOrg[0] : errors.schemaOrg}</p>}
            {!isValidJson(formData.schemaOrg) && !errors.schemaOrg && (<p className='text-sm text-destructive'>Invalid JSON format.</p>)}
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>Industry-Specific Data (JSON)</CardTitle>
          <CardDescription>Custom JSON data relevant to the page's industry type.</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-2">
            <div className="flex items-center gap-1">
              <Label htmlFor='industryData'>Industry Data JSON</Label>
              <InfoTooltip content={AdvancedSeoFormTooltips.industryData} />
            </div>
            {currentIndustryTemplates.length > 0 && (
              <div className="flex items-center space-x-2 mb-2">
                <Select value={selectedIndustryTemplate} onValueChange={(value) => {
                  if (value) handleTemplateSelect('industryData', value);
                  setSelectedIndustryTemplate(value);
                }}>
                  <SelectTrigger className="w-[300px]">
                    <SelectValue placeholder="Select an Industry Data template (optional)" />
                  </SelectTrigger>
                  <SelectContent>
                    {currentIndustryTemplates.map((template: { label: string; value: string }) => (
                      <SelectItem key={template.label} value={template.value}>{template.label}</SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            )}
            <Textarea
              id='industryData'
              name='industryData'
              value={formData.industryData || ''}
              onChange={handleChange}
              rows={10}
              className={`${errors.industryData || !isValidJson(formData.industryData) ? 'border-destructive' : ''} font-mono text-sm`}
              placeholder='{\n  "productDetails": {\n    "sku": "ABC123",\n    "manufacturer": "Example Corp"\n  }\n}'
            />
            {errors.industryData && <p className='text-sm text-destructive'>{Array.isArray(errors.industryData) ? errors.industryData[0] : errors.industryData}</p>}
            {!isValidJson(formData.industryData) && !errors.industryData && (<p className='text-sm text-destructive'>Invalid JSON format.</p>)}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import type React from 'react';
// Removed useEffect, useState, useRouter, updateSeoEntry, ServerActionResult, SeoFormData (will be handled by parent)
import { Info } from 'lucide-react';
import { EntityType, IndustryType } from '@prisma/client'; // Keep for displaying EntityType and IndustryType values

import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from '@/components/ui/tooltip';

// Define the shape of the form data this component handles
export type BasicSeoFormDataSubset = {
  entityId: string; // Read-only on this form, set by parent
  entityType: EntityType; // Read-only on this form, set by parent
  industryType: IndustryType;
  metaTitle: string;
  metaDescription: string;
  canonicalUrl: string;
  robots: string;
  keywords: string[]; // Will be handled as a comma-separated string in input
};

type BasicSeoFormFieldsProps = {
  formData: BasicSeoFormDataSubset;
  errors: Record<string, string[] | undefined>;
  // isSubmitting: boolean; // Handled by parent
  handleChange: (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => void;
  handleSelectValueChange: (name: keyof BasicSeoFormDataSubset, value: string) => void;
  handleKeywordsChange: (value: string) => void; // Parent will handle parsing to array
};

export default function BasicSeoFormFields({
  formData,
  errors,
  handleChange,
  handleSelectValueChange,
  handleKeywordsChange,
}: BasicSeoFormFieldsProps) {

  const formatKeywordsForInput = (keywords: string[] | undefined): string => {
    return keywords ? keywords.join(', ') : '';
  };

  return (
    <> {/* Changed from form to Fragment */}
      <div className='grid grid-cols-1 gap-x-6 gap-y-8 md:grid-cols-2'>
        <div className="space-y-2">
          <Label htmlFor='entityIdDisplay' className="flex items-center">Entity ID / Slug</Label>
          <Input
            type='text'
            id='entityIdDisplay'
            name='entityId' // Name still useful for potential future direct handling
            value={formData.entityId}
            readOnly
            className="bg-muted"
          />
          <p className='text-sm text-muted-foreground'>Identifier for the page, product, or category.</p>
        </div>

        <div className="space-y-2">
          <Label htmlFor='entityTypeDisplay' className="flex items-center">Entity Type</Label>
          <Input
            type="text"
            id="entityTypeDisplay"
            value={formData.entityType.charAt(0) + formData.entityType.slice(1).toLowerCase().replace(/_/g, ' ')}
            readOnly
            className="bg-muted"
          />
          <p className='text-sm text-muted-foreground'>Type of content (Page, Product, Category, etc.).</p>
        </div>
      </div>

      <div className="space-y-2">
        <Label htmlFor='industryType' className="flex items-center">Industry Type</Label>
        <Select
          name="industryType"
          value={formData.industryType}
          onValueChange={(value) => handleSelectValueChange('industryType', value as IndustryType)}
        >
          <SelectTrigger id="industryType" className={`${errors.industryType ? 'border-destructive' : ''}`}>
            <SelectValue placeholder="Select industry type" />
          </SelectTrigger>
          <SelectContent>
            {Object.values(IndustryType).map((typeValue: IndustryType) => (
              <SelectItem key={typeValue} value={typeValue}>
                {typeValue.charAt(0) + typeValue.slice(1).toLowerCase()}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
        {errors.industryType && <p className='text-sm text-destructive'>{errors.industryType[0]}</p>}
      </div>

      <div className="space-y-2">
        <Label htmlFor='metaTitle' className="flex items-center">Meta Title</Label>
        <Input
          type='text'
          id='metaTitle'
          name='metaTitle'
          value={formData.metaTitle}
          onChange={handleChange}
          maxLength={120}
          className={`${errors.metaTitle ? 'border-destructive' : ''}`}
        />
        {errors.metaTitle && <p className='text-sm text-destructive'>{errors.metaTitle[0]}</p>}
        <p className='text-sm text-muted-foreground'>Max 120 chars. Current: {formData.metaTitle?.length || 0}/120</p>
      </div>

      <div className="space-y-2">
        <Label htmlFor='metaDescription' className="flex items-center">Meta Description</Label>
        <Textarea
          id='metaDescription'
          name='metaDescription'
          value={formData.metaDescription}
          onChange={handleChange}
          maxLength={320}
          rows={3}
          className={`${errors.metaDescription ? 'border-destructive' : ''}`}
        />
        {errors.metaDescription && <p className='text-sm text-destructive'>{errors.metaDescription[0]}</p>}
        <p className='text-sm text-muted-foreground'>Max 320 chars. Current: {formData.metaDescription?.length || 0}/320</p>
      </div>

      <div className="space-y-2">
        <Label htmlFor='canonicalUrl' className="flex items-center">Canonical URL</Label>
        <Input
          type='text'
          id='canonicalUrl'
          name='canonicalUrl'
          value={formData.canonicalUrl || ''}
          onChange={handleChange}
          className={`${errors.canonicalUrl ? 'border-destructive' : ''}`}
          placeholder='https://example.com/preferred-page-url'
        />
        {errors.canonicalUrl && <p className='text-sm text-destructive'>{errors.canonicalUrl[0]}</p>}
      </div>

      <div className="space-y-2">
        <Label htmlFor='robots' className="flex items-center">Robots Directive</Label>
        <Select
          name="robots"
          value={formData.robots}
          onValueChange={(value) => handleSelectValueChange('robots', value)}
        >
          <SelectTrigger id="robots" className={`${errors.robots ? 'border-destructive' : ''}`}>
            <SelectValue placeholder="Select robots directive" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value='index, follow'>index, follow (Recommended)</SelectItem>
            <SelectItem value='noindex, follow'>noindex, follow</SelectItem>
            <SelectItem value='index, nofollow'>index, nofollow</SelectItem>
            <SelectItem value='noindex, nofollow'>noindex, nofollow</SelectItem>
          </SelectContent>
        </Select>
        {errors.robots && <p className='text-sm text-destructive'>{errors.robots[0]}</p>}
      </div>

      <div className="space-y-2">
        <Label htmlFor='keywords' className="flex items-center">Keywords</Label>
        <Input
          type='text'
          id='keywords'
          name='keywords' // Name is 'keywords' but value is string, parent handles conversion
          value={formatKeywordsForInput(formData.keywords)}
          onChange={(e) => handleKeywordsChange(e.target.value)}
          className={`${errors.keywords ? 'border-destructive' : ''}`}
          placeholder='e.g., e-commerce, saudi arabia, online shopping'
        />
        {errors.keywords && <p className='text-sm text-destructive'>{errors.keywords[0]}</p>}
        <p className='text-sm text-muted-foreground'>Comma-separated keywords for this page.</p>
      </div>
      {/* Submit button removed */}
    </>
  );
}

'use client';

import type React from 'react';
import { Trash2 } from 'lucide-react';
import { LocalizationSeoFormTooltips } from '@/app/seo/constant/localization-tooltip-content';
import InfoTooltip from '@/components/ui/InfoTooltip';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
// Tooltip components removed as InfoTooltip is used

export type LocalizationSeoFormDataSubset = {
  defaultLanguage: string;
  supportedLanguages: string[];
  hreflang: string; // Expecting stringified JSON
};

type LocalizationSeoFormFieldsProps = {
  formData: LocalizationSeoFormDataSubset;
  errors: Record<string, string[] | string | undefined>;
  handleChange: (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => void;
  handleArrayChange: (name: keyof Pick<LocalizationSeoFormDataSubset, 'supportedLanguages'>, value: string[]) => void;
  addToArray: (name: keyof Pick<LocalizationSeoFormDataSubset, 'supportedLanguages'>, inputId: string) => void;
  removeFromArray: (name: keyof Pick<LocalizationSeoFormDataSubset, 'supportedLanguages'>, index: number) => void;
};

export default function LocalizationSeoFormFields({
  formData,
  errors,
  handleChange,
  handleArrayChange,
  addToArray,
  removeFromArray,
}: LocalizationSeoFormFieldsProps) {

  const isValidJson = (str: string | null | undefined): boolean => {
    if (str === null || str === undefined || str.trim() === '') return true;
    try {
      JSON.parse(str);
      return true;
    } catch {
      return false;
    }
  }; // Ensured semicolon

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle>Language Settings</CardTitle>
          <CardDescription>Configure default and supported languages for this content.</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="space-y-2">
            <div className="flex items-center gap-1">
              <Label htmlFor='defaultLanguage'>Default Language</Label>
              <InfoTooltip content={LocalizationSeoFormTooltips.defaultLanguage} />
            </div>
            <Input
              type='text'
              id='defaultLanguage'
              name='defaultLanguage'
              value={formData.defaultLanguage}
              onChange={handleChange}
              className={`${errors.defaultLanguage ? 'border-destructive' : ''}`}
              placeholder='e.g., ar-SA'
            />
            {errors.defaultLanguage && <p className='text-sm text-destructive'>{Array.isArray(errors.defaultLanguage) ? errors.defaultLanguage[0] : errors.defaultLanguage}</p>}
          </div>

          <div className="space-y-2">
            <div className="flex items-center gap-1">
              <Label htmlFor='supportedLanguages-group'>Supported Languages</Label>
              <InfoTooltip content={LocalizationSeoFormTooltips.supportedLanguages} />
            </div>
            {(formData.supportedLanguages || []).map((lang, index) => (
              <div key={`lang-${index}`} className="flex items-center space-x-2">
                <Input
                  type="text"
                  value={lang}
                  onChange={(e) => {
                    const newLangs = [...(formData.supportedLanguages || [])];
                    newLangs[index] = e.target.value;
                    handleArrayChange('supportedLanguages', newLangs);
                  }}
                  placeholder="e.g., en-US"
                  className={`${(errors.supportedLanguages as string[] | undefined)?.[index] ? 'border-destructive' : ''}`}
                />
                <Button type="button" variant="outline" size="icon" onClick={() => removeFromArray('supportedLanguages', index)}>
                  <Trash2 className="h-4 w-4" />
                </Button>
              </div>
            ))}
            <div className="flex items-center space-x-2">
              <Input
                type="text"
                id="newSupportedLanguage"
                placeholder="Add language code"
                onKeyDown={(e) => {
                  if (e.key === 'Enter' && e.currentTarget.value.trim()) {
                    e.preventDefault();
                    addToArray('supportedLanguages', 'newSupportedLanguage');
                  }
                }}
              />
              <Button type="button" onClick={() => addToArray('supportedLanguages', 'newSupportedLanguage')}>
                Add Language
              </Button>
            </div>
            {errors.supportedLanguages && (
              <p className='text-sm text-destructive'>
                {Array.isArray(errors.supportedLanguages) ? errors.supportedLanguages.join(', ') : errors.supportedLanguages}
              </p>
            )}
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>Hreflang Configuration</CardTitle>
          <CardDescription>Define alternate language versions for search engines.</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="space-y-2">
            <div className="flex items-center gap-1">
              <Label htmlFor='hreflang'>Hreflang Links (JSON)</Label>
              <InfoTooltip content={LocalizationSeoFormTooltips.hreflang} />
            </div>
            <Textarea
              id='hreflang'
              name='hreflang'
              value={formData.hreflang || ''}
              onChange={handleChange}
              rows={5}
              className={`${errors.hreflang || !isValidJson(formData.hreflang) ? 'border-destructive' : ''} font-mono text-sm`}
              placeholder='{"en-US": "https://example.com/en/my-page", "es-ES": "https://example.com/es/mi-pagina"}'
            />
            {errors.hreflang && <p className='text-sm text-destructive'>{Array.isArray(errors.hreflang) ? errors.hreflang[0] : errors.hreflang}</p>}
            {!isValidJson(formData.hreflang) && !errors.hreflang && (<p className='text-sm text-destructive'>Invalid JSON format.</p>)}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import type React from 'react';
import { SocialSeoFormTooltips } from '@/app/seo/constant/social-tooltip-content';
import InfoTooltip from '@/components/ui/InfoTooltip';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'; // Import Card components
